const mongoose = require('mongoose');

const empanadaSchema = mongoose.Schema(
  {
    // campos
    email: {
      type: String,
      trim: true,
      lowercase: true,
      unique: true,
      required: [true, 'Email address is required'],
      match: [/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/, 'Please fill a valid email address']},

    firstname: { type: String, required: [true, 'Please provide a name'] },

    secondname:{ type: String, required: [true, 'Please provide a second name'] },

    password: { type: String, required: [true, 'Please provide a password'] },

    numerocel: { type: Number , required: [true, 'Please provide a phone number']},

    tipo: {
      type: String,
      enum: ['cliente', 'admin', 'domiciliario'],
      required: [true, 'Please select a valid user type']
    },

    address: { type: String, required: [true, 'Please provide a password']},

    isDeleted: { type: Boolean, default: false },
  },
  { timestamps: true }
);

export default mongoose.model('empanada', empanadaSchema);

