import User from './user.model';

export async function getUser(req, res) {
  const email = req.body.email;

  const password = req.body.password;

  User.findOne({ email: email }).then((user) => {
    if (!user) {
      // El usuario no existe en la base de datos
      res.status(401).send({ message: 'Correo electrónico o contraseña incorrectos' });
    } else {
      // Verificar la contraseña del usuario
      if (user.password === password) {
        // La contraseña es correcta
        res.status(200).send({ message: 'Autenticado correctamente' });
      } else {
        // La contraseña es incorrecta
        res.status(401).send({ message: 'Contraseña incorrecta' });
      }
    }
  });
}

export async function createUser(req, res) {
  try {
    const { firstname } = req.body;
    const { secondname } = req.body;
    const { email } = req.body;
    const { password } = req.body;
    const { numerocel } = req.body;
    const { tipo } = req.body;
    const { address } = req.body;
    const user = new User({
      firstname,
      secondname,
      email,
      password,
      numerocel,
      tipo,
      address,
    });
    const resultado = await user.save();
    res.status(200).json(resultado);
  } catch (err) {
    res.status(500).json(err);
  }
}

export async function patchUser(req, res) {
  const id = req.body.id;
  const { firstname } = req.body;
  const { secondname } = req.body;
  const { email } = req.body;
  const { password } = req.body;
  const { numerocel } = req.body;
  const { tipo } = req.body;
  const { address } = req.body;

  const actualizado = await User.updateOne(
    { _id: id },
    {
      firstname: firstname,
      secondname: secondname,
      email: email,
      password: password,
      numerocel: numerocel,
      tipo: tipo,
      address: address,
    }
  );
  // FIX "Para que muestre el update exitoso"
  res.status(200).send(await User.findById({ _id: id }));
}

export async function deleteUser(req, res) {
  const id = req.body.id;
  const borrado = true;
  //***** */ FIX a que sea soft delete y no delete directo**** //
  User.updateOne(
    { _id: id },
    {
      isDeleted: borrado,
    }
  );
  res.send({
    message: 'Usuario borrado existosamente!',
  });
}

