import Restaurante from './restaurante.model';

export async function getRestaurante(req, res) {
  const nombre = req.query.nombre;
  const categoria = req.query.categoria;
  const resid = req.query.resid;

  // Se crea un objeto vacío que se usará para filtrar la búsqueda
  let filtro = {};

  if (nombre) {
    // Se utiliza una expresión regular para buscar el nombre en cualquier parte de la cadena
    filtro.nombre = { $regex: nombre, $options: 'i' };
  }
  if (categoria) {
    filtro.categorias = categoria;
  }
  if (resid) {
    // Se utiliza una expresión regular para buscar el nombre en cualquier parte de la cadena
    filtro.resid = resid;
  }

  Restaurante.find(filtro)
    .sort({ numpedidos: 'descending' })
    .then((restaurantes) => {
      if (restaurantes.length === 0) {
        // No se encontraron restaurantes que coincidan con los criterios de búsqueda
        res.status(404).send({
          message: 'No se encontraron restaurantes con los criterios de búsqueda proporcionados',
        });
      } else {
        // Se encontraron restaurantes, se devuelve su información
        res.status(200).send({ restaurantes });
      }
    })
    .catch((error) => {
      // Error en la consulta de la base de datos
      res.status(500).send({ message: 'Error al obtener los restaurantes: ' + error });
    });
}

export async function createRestaurante(req, res) {
  try {
    const { nombre } = req.body;
    const { numerocel } = req.body;
    const { categorias } = req.body;
    const { address } = req.body;
    const restaurante = new Restaurante({
      nombre,
      numerocel,
      categorias,
      address,
    });
    const resultado = await restaurante.save();
    res.status(200).json(resultado);
  } catch (err) {
    res.status(500).json(err);
  }
}

export async function patchRestaurante(req, res) {
  const id = req.body.id;
  const { nombre } = req.body;
  const { numerocel } = req.body;
  const { categorias } = req.body;
  const { address } = req.body;

  const actualizado = await Restaurante.updateOne(
    { _id: id },
    {
      nombre: nombre,
      numerocel: numerocel,
      categorias: categorias,
      address: address,
    }
  );
  // FIX "Para que muestre el update exitoso"
  res.status(200).send(await Restaurante.findById({ _id: id }));
}

export async function deleteRestaurante(req, res) {
  const id = req.body.id;
  const borrado = true;
  // Soft Delete
  const producto = await Restaurante.findOneAndUpdate(
    { _id: id },
    { isDeleted: borrado },
    { new: true }
  );
  if (producto) {
    res.send({
      message: 'Restaurant deleted succesfully!',
    });
  } else {
    res.status(404).send({
      message: 'Restaurant not found',
    });
  }
}

