import Producto from './producto.model';
import Restaurante from '../restaurante/restaurante.model';
export async function getProducto(req, res) {
  const nombre = req.query.nombre;
  const categoria = req.query.categoria;

  // Se crea un objeto vacío que se usará para filtrar la búsqueda
  let filtro = {};

  if (nombre) {
    filtro.nombre = nombre;
  }
  if (categoria) {
    filtro.categorias = categoria;
  }

  Producto.find(filtro)
    .then((productos) => {
      if (productos.length === 0) {
        // No se encontraron productos que coincidan con los criterios de búsqueda
        res.status(404).send({
          message: 'No se encontraron productos con los criterios de búsqueda proporcionados',
        });
      } else {
        // Se encontraron productos, se devuelve su información
        res.status(200).send({ productos });
      }
    })
    .catch((error) => {
      // Error en la consulta de la base de datos
      res.status(500).send({ message: 'Error al obtener los productos: ' + error });
    });
}

export async function createProducto(req, res) {
  try {
    const restauranteId = req.body.restauranteId; // Obtener el ID del restaurante del cuerpo de la solicitud
    const restaurante = await Restaurante.findById(restauranteId); // Buscar el restaurante correspondiente

    const { nombre, descripcion, categorias, precio } = req.body; // Obtener los datos del producto desde el cuerpo de la solicitud
    const producto = new Producto({
      nombre,
      descripcion,
      categorias,
      precio,
      restaurante: restaurante._id,
      nombreRestaurante: restaurante.nombre, // Agregar el ID del restaurante al objeto de producto
    });

    const resultado = await producto.save(); // Guardar el nuevo producto en la base de datos
    restaurante.productos.push(producto._id); // Agregar el ID del producto al arreglo de productos del restaurante
    await restaurante.save(); // Guardar los cambios en el restaurante

    res.status(200).json(resultado); // Enviar una respuesta con el nuevo producto guardado
  } catch (err) {
    res.status(500).json(err); // Enviar una respuesta con el error si algo salió mal
  }
}

export async function patchProducto(req, res) {
  const id = req.body.id;
  const { nombre } = req.body;
  const { descripcion } = req.body;
  const { categorias } = req.body;
  const { precio } = req.body;

  const actualizado = await Producto.updateOne(
    { _id: id },
    {
      nombre: nombre,
      descripcion: descripcion,
      categorias: categorias,
      precio: precio,
    }
  );
  // FIX "Para que muestre el update exitoso"
  res.status(200).send(await Producto.findById({ _id: id }));
}

export async function deleteProducto(req, res) {
  const id = req.body.id;
  const borrado = true;
  // Soft Delete
  const producto = await Producto.findOneAndUpdate(
    { _id: id },
    { isDeleted: borrado },
    { new: true }
  );
  if (producto) {
    res.send({
      message: 'Product deleted succesfully!',
    });
  } else {
    res.status(404).send({
      message: 'Product not found',
    });
  }
}

